#include <iostream>
using namespace std;

bool isValid(char * s){

	if (NULL == s || s == "")
		return true;

	char tmp[10000];
	int i = 0;
	int index = -1;

	while (s[i] != '\0')
	{

		tmp[++index] = s[i];
		if ((s[i] == ')'))
		{
			if (((index-1)<0) || (tmp[index-1] != '('))
			{
				return false;
			}
			else
			{
				index = index-2;
			}
		}
		else if ((s[i] == ']'))
		{
			if (((index - 1)<0) || (tmp[index-1] != '['))
			{
				return false;
			}
			else
			{
				index = index - 2;
			}
		}
		else if ((s[i] == '}'))
		{
			if (((index - 1)<0) || tmp[index-1] != '{')
			{
				return false;
			}
			else
			{
				index = index - 2;
			}
		}
		i++;
	}

	if (index!=-1)
	{
		return false;
	}
	else
	{
		return true;
	}
	

}

